import React, { useState } from 'react';
import axiosInstance from '../api/axios';
import './Reports.css';
import './PremiumManagerPages.css';

const Reports = () => {
  const [filters, setFilters] = useState({ start: '', end: '', employeeId: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [reportData, setReportData] = useState(null);
  const [exporting, setExporting] = useState(false);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleShowTable = async () => {
    if (!filters.start || !filters.end) {
      setError('Please select both start and end dates');
      return;
    }

    try {
      setError('');
      setLoading(true);
      const response = await axiosInstance.get('/attendance/all', {
        params: { ...filters, page: 1, limit: 100 }
      });
      setReportData(response.data.attendance);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch report');
      setReportData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    if (!filters.start || !filters.end) {
      setError('Please select both start and end dates');
      return;
    }

    try {
      setError('');
      setExporting(true);
      const response = await axiosInstance.get('/attendance/export', {
        params: filters,
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `attendance-report-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.parentElement.removeChild(link);
    } catch (err) {
      setError(err.response?.data?.error || 'Export failed');
    } finally {
      setExporting(false);
    }
  };

  return (
    <div className="container">
      <div className="reports-header">
        <h1>Attendance Reports</h1>
        <p className="reports-subtitle">Generate and export attendance reports for analysis</p>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      <div className="reports-container">
        {/* Report Generator */}
        <div className="report-generator">
          <div className="report-generator-title">üìä Generate Report</div>

          <div className="form-group">
            <label>Start Date</label>
            <input
              type="date"
              name="start"
              value={filters.start}
              onChange={handleFilterChange}
            />
            <span className="help-text">Select the start date for the report</span>
          </div>

          <div className="form-group">
            <label>End Date</label>
            <input
              type="date"
              name="end"
              value={filters.end}
              onChange={handleFilterChange}
            />
            <span className="help-text">Select the end date for the report</span>
          </div>

          <div className="form-group">
            <label>Employee ID (Optional)</label>
            <input
              type="text"
              name="employeeId"
              value={filters.employeeId}
              onChange={handleFilterChange}
              placeholder="e.g., EMP001"
            />
            <span className="help-text">Leave empty to include all employees</span>
          </div>

          <div className="button-group">
            <button
              className="btn-show-table"
              onClick={handleShowTable}
              disabled={loading}
            >
              {loading ? '‚è≥ Loading...' : 'üìã Show Table'}
            </button>
            <button
              className="btn-export-csv"
              onClick={handleExport}
              disabled={exporting}
            >
              {exporting ? '‚è≥ Exporting...' : 'üì• Export CSV'}
            </button>
          </div>
        </div>

        {/* Report Information */}
        <div className="report-info">
          <div className="report-info-title">‚ÑπÔ∏è Report Information</div>
          <div className="info-content">
            <p>
              Generate comprehensive attendance reports for a specific date range. Filter by employee ID to get individual reports.
            </p>
            <p><strong>Report includes:</strong></p>
            <ul>
              <li>Employee ID, Name, Email, Department</li>
              <li>Attendance Date</li>
              <li>Check In and Check Out Times</li>
              <li>Attendance Status (Present, Absent, Late, Half-Day)</li>
              <li>Total Hours Worked</li>
            </ul>
            <p><strong>Export Formats:</strong></p>
            <ul>
              <li>CSV format for easy import to Excel or other tools</li>
              <li>Includes all filtered records</li>
              <li>Ready for analysis and archival</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Report Results */}
      {reportData && (
        <div className="report-results">
          <div className="results-header">
            <h3>Report Results</h3>
            <div className="results-count">{reportData.length} records</div>
          </div>

          {reportData.length > 0 ? (
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Employee ID</th>
                    <th>Name</th>
                    <th>Department</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Check In</th>
                    <th>Check Out</th>
                    <th>Hours</th>
                  </tr>
                </thead>
                <tbody>
                  {reportData.map((record) => (
                    <tr key={record._id}>
                      <td><strong>{record.userId.employeeId}</strong></td>
                      <td>{record.userId.name}</td>
                      <td>{record.userId.department}</td>
                      <td>{record.date}</td>
                      <td><span className={`badge badge-${record.status.replace('-', '-')}`}>{record.status}</span></td>
                      <td>{record.checkInTime ? new Date(record.checkInTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : '-'}</td>
                      <td>{record.checkOutTime ? new Date(record.checkOutTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : '-'}</td>
                      <td>{record.totalHours ? `${record.totalHours}h` : '-'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="no-results">No records found for the selected criteria</div>
          )}
        </div>
      )}
    </div>
  );
};

export default Reports;
