import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';

const AllAttendance = () => {
  const [attendance, setAttendance] = useState([]);
  const [filters, setFilters] = useState({ employeeId: '', date: '', status: '' });
  const [page, setPage] = useState(1);
  const [pagination, setPagination] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchAttendance = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get('/attendance/all', {
        params: { ...filters, page, limit: 30 }
      });
      setAttendance(response.data.attendance);
      setPagination(response.data.pagination);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch attendance');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
    setPage(1);
  };

  const handleExport = async () => {
    try {
      const response = await axiosInstance.get('/attendance/export', {
        params: filters,
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'attendance-export.csv');
      document.body.appendChild(link);
      link.click();
      link.parentElement.removeChild(link);
    } catch (err) {
      setError('Export failed');
    }
  };

  if (loading) return <div className="container"><p>Loading...</p></div>;

  return (
    <div className="container">
      <h1>All Attendance Records</h1>

      {error && <div className="alert alert-error">{error}</div>}

      <div className="card">
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', marginBottom: '1rem' }}>
          <div className="form-group">
            <label>Employee ID</label>
            <input
              type="text"
              name="employeeId"
              value={filters.employeeId}
              onChange={handleFilterChange}
              placeholder="EMP001"
            />
          </div>
          <div className="form-group">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={filters.date}
              onChange={handleFilterChange}
            />
          </div>
          <div className="form-group">
            <label>Status</label>
            <select name="status" value={filters.status} onChange={handleFilterChange}>
              <option value="">All</option>
              <option value="present">Present</option>
              <option value="absent">Absent</option>
              <option value="late">Late</option>
              <option value="half-day">Half Day</option>
            </select>
          </div>
        </div>

        <button className="btn btn-secondary" onClick={handleExport} style={{ marginBottom: '1rem' }}>
          Export to CSV
        </button>

        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Name</th>
                <th>Department</th>
                <th>Date</th>
                <th>Status</th>
                <th>Check In</th>
                <th>Check Out</th>
                <th>Hours</th>
              </tr>
            </thead>
            <tbody>
              {attendance.map((record) => (
                <tr key={record._id}>
                  <td>{record.userId.employeeId}</td>
                  <td>{record.userId.name}</td>
                  <td>{record.userId.department}</td>
                  <td>{record.date}</td>
                  <td><span className={`badge badge-${record.status.replace('-', '-')}`}>{record.status}</span></td>
                  <td>{record.checkInTime ? new Date(record.checkInTime).toLocaleTimeString() : '-'}</td>
                  <td>{record.checkOutTime ? new Date(record.checkOutTime).toLocaleTimeString() : '-'}</td>
                  <td>{record.totalHours || '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {pagination && (
          <div className="pagination">
            <button onClick={() => setPage(1)} disabled={page === 1}>First</button>
            <button onClick={() => setPage(page - 1)} disabled={page === 1}>Prev</button>
            <span style={{ padding: '0.5rem' }}>Page {page} of {pagination.pages}</span>
            <button onClick={() => setPage(page + 1)} disabled={page === pagination.pages}>Next</button>
            <button onClick={() => setPage(pagination.pages)} disabled={page === pagination.pages}>Last</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AllAttendance;
