import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';
import './AttendanceMark.css';
import './PremiumAttendanceMark.css';

const AttendanceMark = () => {
  const [todayAttendance, setTodayAttendance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [checkingIn, setCheckingIn] = useState(false);
  const [checkingOut, setCheckingOut] = useState(false);

  useEffect(() => {
    fetchTodayAttendance();
  }, []);

  const fetchTodayAttendance = async () => {
    try {
      const response = await axiosInstance.get('/attendance/today');
      setTodayAttendance(response.data);
    } catch (err) {
      if (err.response?.status === 404) {
        setTodayAttendance(null);
      } else {
        setError(err.response?.data?.error || 'Failed to fetch attendance');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCheckIn = async () => {
    try {
      setError('');
      setSuccess('');
      setCheckingIn(true);
      const response = await axiosInstance.post('/attendance/checkin');
      setTodayAttendance(response.data.attendance);
      setSuccess('Checked in successfully!');
    } catch (err) {
      setError(err.response?.data?.error || 'Check-in failed');
    } finally {
      setCheckingIn(false);
    }
  };

  const handleCheckOut = async () => {
    try {
      setError('');
      setSuccess('');
      setCheckingOut(true);
      const response = await axiosInstance.post('/attendance/checkout');
      setTodayAttendance(response.data.attendance);
      setSuccess('Checked out successfully!');
    } catch (err) {
      setError(err.response?.data?.error || 'Check-out failed');
    } finally {
      setCheckingOut(false);
    }
  };

  if (loading) return <div className="container"><p>Loading...</p></div>;

  return (
    <div className="container">
      <div className="mark-attendance-header">
        <h1>Mark Attendance</h1>
        <p className="subtitle">Check in and out to track your working hours</p>
      </div>

      {error && <div className="alert alert-error">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}

      <div className="mark-attendance-container">
        {!todayAttendance ? (
          <div className="no-checkin-card">
            <div className="icon">üìç</div>
            <h2>Ready to Check In?</h2>
            <p>You haven't checked in today yet. Click the button below to get started.</p>
            <button 
              className="btn btn-success btn-xl" 
              onClick={handleCheckIn}
              disabled={checkingIn}
            >
              {checkingIn ? 'Checking In...' : '‚úì Check In Now'}
            </button>
          </div>
        ) : (
          <div className="attendance-status-card">
            <div className="status-header">
              <h2>Today's Attendance</h2>
              <span className={`status-badge badge-${todayAttendance.status.replace('-', '-')}`}>
                {todayAttendance.status}
              </span>
            </div>

            <div className="status-details">
              <div className="detail-row">
                <span className="detail-label">Check In Time:</span>
                <span className="detail-value">
                  {new Date(todayAttendance.checkInTime).toLocaleTimeString()}
                </span>
              </div>

              {todayAttendance.checkOutTime ? (
                <>
                  <div className="detail-row">
                    <span className="detail-label">Check Out Time:</span>
                    <span className="detail-value">
                      {new Date(todayAttendance.checkOutTime).toLocaleTimeString()}
                    </span>
                  </div>
                  <div className="detail-row highlight">
                    <span className="detail-label">Total Hours:</span>
                    <span className="detail-value">{todayAttendance.totalHours} hrs</span>
                  </div>
                  <div className="completed-message">
                    ‚úì You have completed your attendance for today.
                  </div>
                </>
              ) : (
                <>
                  <div className="detail-row">
                    <span className="detail-label">Time Elapsed:</span>
                    <span className="detail-value">
                      {Math.floor((new Date() - new Date(todayAttendance.checkInTime)) / 3600000)} hrs
                    </span>
                  </div>
                  <button 
                    className="btn btn-danger btn-xl" 
                    onClick={handleCheckOut}
                    disabled={checkingOut}
                  >
                    {checkingOut ? 'Checking Out...' : '‚úó Check Out Now'}
                  </button>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AttendanceMark;
