import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axios';
import './MyAttendance.css';
import './PremiumAttendance.css';

const MyAttendance = () => {
  const [attendance, setAttendance] = useState([]);
  const [month, setMonth] = useState(new Date().toISOString().slice(0, 7));
  const [page, setPage] = useState(1);
  const [pagination, setPagination] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [viewMode, setViewMode] = useState('calendar'); // 'calendar' or 'list'

  const fetchAttendance = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get('/attendance/my-history', {
        params: { month, page, limit: 30 }
      });
      setAttendance(response.data.attendance);
      setPagination(response.data.pagination);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch attendance');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAttendance();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [month, page]);

  if (loading) return <div className="container"><p>Loading...</p></div>;

  const getDaysInMonth = (year, month) => new Date(year, month, 0).getDate();
  const getFirstDayOfMonth = (year, month) => new Date(year, month - 1, 1).getDay();

  const [year, monthNum] = month.split('-');
  const daysInMonth = getDaysInMonth(year, monthNum);
  const firstDay = getFirstDayOfMonth(year, monthNum);

  const attendanceMap = {};
  attendance.forEach(record => {
    attendanceMap[record.date] = record;
  });

  const getStatusColor = (status) => {
    switch (status) {
      case 'present': return 'green';
      case 'absent': return 'red';
      case 'late': return 'yellow';
      case 'half-day': return 'orange';
      default: return 'gray';
    }
  };

  const selectedRecord = selectedDate ? attendanceMap[selectedDate] : null;

  const calendarDays = [];
  for (let i = 0; i < firstDay; i++) {
    calendarDays.push(null);
  }
  for (let day = 1; day <= daysInMonth; day++) {
    const dateStr = `${year}-${String(monthNum).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
    calendarDays.push(dateStr);
  }

  return (
    <div className="container">
      <div className="attendance-header">
        <h1>My Attendance</h1>
        <p className="subtitle">Track your attendance with calendar and detailed view</p>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      <div className="attendance-controls">
        <div className="month-selector">
          <label>Month:</label>
          <input
            type="month"
            value={month}
            onChange={(e) => { setMonth(e.target.value); setPage(1); setSelectedDate(null); }}
          />
        </div>

        <div className="view-toggle">
          <button 
            className={`toggle-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            ðŸ“… Calendar
          </button>
          <button 
            className={`toggle-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => setViewMode('list')}
          >
            ðŸ“‹ List
          </button>
        </div>
      </div>

      {viewMode === 'calendar' ? (
        <div className="calendar-view">
          <div className="card">
            <div className="calendar-container">
              <div className="calendar-header">
                <h3>{new Date(year, monthNum - 1).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</h3>
              </div>

              <div className="weekdays">
                {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                  <div key={day} className="weekday">{day}</div>
                ))}
              </div>

              <div className="calendar-grid">
                {calendarDays.map((date, idx) => {
                  const record = date ? attendanceMap[date] : null;
                  const day = date ? parseInt(date.split('-')[2]) : null;
                  return (
                    <div
                      key={idx}
                      className={`calendar-day ${!date ? 'empty' : ''} ${selectedDate === date ? 'selected' : ''}`}
                      onClick={() => date && setSelectedDate(date)}
                      style={record ? { backgroundColor: getStatusColor(record.status) } : {}}
                    >
                      {day && (
                        <>
                          <div className="day-number">{day}</div>
                          {record && <div className="day-status">{record.status.charAt(0).toUpperCase()}</div>}
                        </>
                      )}
                    </div>
                  );
                })}
              </div>

              <div className="legend">
                <div className="legend-item"><span className="legend-color green"></span> Present</div>
                <div className="legend-item"><span className="legend-color red"></span> Absent</div>
                <div className="legend-item"><span className="legend-color yellow"></span> Late</div>
                <div className="legend-item"><span className="legend-color orange"></span> Half-Day</div>
              </div>
            </div>

            {selectedRecord && (
              <div className="detail-panel">
                <h4>Details for {selectedDate}</h4>
                <div className="detail-content">
                  <div className="detail-item">
                    <span className="label">Status:</span>
                    <span className={`badge badge-${selectedRecord.status.replace('-', '-')}`}>
                      {selectedRecord.status}
                    </span>
                  </div>
                  {selectedRecord.checkInTime && (
                    <div className="detail-item">
                      <span className="label">Check In:</span>
                      <span>{new Date(selectedRecord.checkInTime).toLocaleTimeString()}</span>
                    </div>
                  )}
                  {selectedRecord.checkOutTime && (
                    <div className="detail-item">
                      <span className="label">Check Out:</span>
                      <span>{new Date(selectedRecord.checkOutTime).toLocaleTimeString()}</span>
                    </div>
                  )}
                  {selectedRecord.totalHours && (
                    <div className="detail-item">
                      <span className="label">Total Hours:</span>
                      <span>{selectedRecord.totalHours} hrs</span>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="list-view">
          <div className="card">
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Check In</th>
                    <th>Check Out</th>
                    <th>Hours</th>
                  </tr>
                </thead>
                <tbody>
                  {attendance.length > 0 ? (
                    attendance.map((record) => (
                      <tr key={record._id}>
                        <td>{record.date}</td>
                        <td><span className={`badge badge-${record.status.replace('-', '-')}`}>{record.status}</span></td>
                        <td>{record.checkInTime ? new Date(record.checkInTime).toLocaleTimeString() : '-'}</td>
                        <td>{record.checkOutTime ? new Date(record.checkOutTime).toLocaleTimeString() : '-'}</td>
                        <td>{record.totalHours || '-'}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" style={{ textAlign: 'center' }}>No records found for this month</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>

            {pagination && pagination.pages > 1 && (
              <div className="pagination">
                <button onClick={() => setPage(1)} disabled={page === 1}>First</button>
                <button onClick={() => setPage(page - 1)} disabled={page === 1}>Prev</button>
                <span style={{ padding: '0.5rem' }}>Page {page} of {pagination.pages}</span>
                <button onClick={() => setPage(page + 1)} disabled={page === pagination.pages}>Next</button>
                <button onClick={() => setPage(pagination.pages)} disabled={page === pagination.pages}>Last</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default MyAttendance;
