import React, { useState } from 'react';
import axiosInstance from '../api/axios';
import './Reports.css';

const Reports = () => {
  const [filters, setFilters] = useState({ start: '', end: '', employeeId: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [reportData, setReportData] = useState(null);
  const [exporting, setExporting] = useState(false);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const handleShowTable = async () => {
    if (!filters.start || !filters.end) {
      setError('Please select both start and end dates');
      return;
    }

    try {
      setError('');
      setLoading(true);
      const response = await axiosInstance.get('/attendance/all', {
        params: { ...filters, page: 1, limit: 100 }
      });
      setReportData(response.data.attendance);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch report');
      setReportData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    if (!filters.start || !filters.end) {
      setError('Please select both start and end dates');
      return;
    }

    try {
      setError('');
      setExporting(true);
      const response = await axiosInstance.get('/attendance/export', {
        params: filters,
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `attendance-report-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.parentElement.removeChild(link);
    } catch (err) {
      setError(err.response?.data?.error || 'Export failed');
    } finally {
      setExporting(false);
    }
  };

  return (
    <div className="container">
      <h1>Reports</h1>

      {error && <div className="alert alert-error">{error}</div>}

      <div className="card">
        <div className="card-title">Generate Attendance Report</div>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', marginBottom: '1rem' }}>
          <div className="form-group">
            <label>Start Date</label>
            <input
              type="date"
              name="start"
              value={filters.start}
              onChange={handleFilterChange}
            />
          </div>
          <div className="form-group">
            <label>End Date</label>
            <input
              type="date"
              name="end"
              value={filters.end}
              onChange={handleFilterChange}
            />
          </div>
          <div className="form-group">
            <label>Employee ID (Optional)</label>
            <input
              type="text"
              name="employeeId"
              value={filters.employeeId}
              onChange={handleFilterChange}
              placeholder="Leave empty for all"
            />
          </div>
        </div>

        <button
          className="btn btn-primary"
          onClick={handleExport}
          disabled={loading}
        >
          {loading ? 'Generating...' : 'Download CSV Report'}
        </button>
      </div>

      <div className="card">
        <div className="card-title">Report Information</div>
        <div className="card-content">
          <p>Generate attendance reports for a specific date range. You can optionally filter by employee ID.</p>
          <p>The report will include:</p>
          <ul style={{ marginLeft: '1.5rem' }}>
            <li>Employee ID, Name, Email, Department</li>
            <li>Date, Check In Time, Check Out Time</li>
            <li>Attendance Status (Present, Absent, Late, Half-Day)</li>
            <li>Total Hours Worked</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Reports;
