import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';

const ManagerDashboard = () => {
  const [dashboard, setDashboard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDashboard();
  }, []);

  const fetchDashboard = async () => {
    try {
      const response = await axiosInstance.get('/dashboard/manager');
      setDashboard(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch dashboard');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="container"><p>Loading...</p></div>;

  return (
    <div className="container">
      <h1>Manager Dashboard</h1>

      {error && <div className="alert alert-error">{error}</div>}

      {dashboard && (
        <>
          <div className="grid">
            <div className="stat-box">
              <div className="stat-value">{dashboard.totalEmployees}</div>
              <div className="stat-label">Total Employees</div>
            </div>
            <div className="stat-box success">
              <div className="stat-value">{dashboard.todayStats.present}</div>
              <div className="stat-label">Present Today</div>
            </div>
            <div className="stat-box danger">
              <div className="stat-value">{dashboard.todayStats.absent}</div>
              <div className="stat-label">Absent Today</div>
            </div>
            <div className="stat-box warning">
              <div className="stat-value">{dashboard.lateArrivals.length}</div>
              <div className="stat-label">Late Arrivals</div>
            </div>
          </div>

          <div className="grid">
            <div className="card">
              <div className="card-title">Late Arrivals Today</div>
              {dashboard.lateArrivals.length === 0 ? (
                <p>No late arrivals</p>
              ) : (
                <div className="table-container">
                  <table>
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Department</th>
                        <th>Check In Time</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dashboard.lateArrivals.map((emp) => (
                        <tr key={emp.employeeId}>
                          <td>{emp.name}</td>
                          <td>{emp.department}</td>
                          <td>{new Date(emp.checkInTime).toLocaleTimeString()}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>

            <div className="card">
              <div className="card-title">Weekly Trend</div>
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Present</th>
                      <th>Absent</th>
                      <th>Late</th>
                    </tr>
                  </thead>
                  <tbody>
                    {dashboard.weeklyTrend.map((day) => (
                      <tr key={day.date}>
                        <td>{day.date}</td>
                        <td>{day.present}</td>
                        <td>{day.absent}</td>
                        <td>{day.late}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="card-title">Department Summary</div>
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Department</th>
                    <th>Present</th>
                    <th>Absent</th>
                    <th>Late</th>
                    <th>Half Day</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(dashboard.departmentSummary).map(([dept, stats]) => (
                    <tr key={dept}>
                      <td>{dept}</td>
                      <td>{stats.present}</td>
                      <td>{stats.absent}</td>
                      <td>{stats.late}</td>
                      <td>{stats.halfDay}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default ManagerDashboard;
