import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [dashboard, setDashboard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchDashboard = async () => {
    try {
      const response = await axiosInstance.get('/dashboard/employee');
      setDashboard(response.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch dashboard');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  const handleCheckIn = async () => {
    try {
      setError('');
      setSuccess('');
      await axiosInstance.post('/attendance/checkin');
      setSuccess('Checked in successfully!');
      fetchDashboard();
    } catch (err) {
      setError(err.response?.data?.error || 'Check-in failed');
    }
  };

  const handleCheckOut = async () => {
    try {
      setError('');
      setSuccess('');
      await axiosInstance.post('/attendance/checkout');
      setSuccess('Checked out successfully!');
      fetchDashboard();
    } catch (err) {
      setError(err.response?.data?.error || 'Check-out failed');
    }
  };

  if (loading) return <div className="container"><p>Loading...</p></div>;

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1>Employee Dashboard</h1>
        <p className="dashboard-subtitle">Welcome back! Track your attendance here.</p>
      </div>

      {error && <div className="alert alert-error">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}

      {dashboard && (
        <>
          {/* Quick Action Buttons */}
          <div className="quick-actions">
            <button className="btn btn-success btn-lg" onClick={handleCheckIn}>
              ✓ Check In
            </button>
            <button className="btn btn-danger btn-lg" onClick={handleCheckOut}>
              ✗ Check Out
            </button>
          </div>

          {/* Today's Status Card */}
          <div className="card today-card">
            <div className="card-title">Today's Status</div>
            <div className="card-content">
              {dashboard.todayStatus.message ? (
                <p className="no-checkin">{dashboard.todayStatus.message}</p>
              ) : (
                <div className="status-info">
                  <div className="status-item">
                    <span className="label">Status:</span>
                    <span className={`badge badge-${dashboard.todayStatus.status.replace('-', '-')}`}>
                      {dashboard.todayStatus.status}
                    </span>
                  </div>
                  {dashboard.todayStatus.checkInTime && (
                    <div className="status-item">
                      <span className="label">Check In:</span>
                      <span>{new Date(dashboard.todayStatus.checkInTime).toLocaleTimeString()}</span>
                    </div>
                  )}
                  {dashboard.todayStatus.checkOutTime && (
                    <div className="status-item">
                      <span className="label">Check Out:</span>
                      <span>{new Date(dashboard.todayStatus.checkOutTime).toLocaleTimeString()}</span>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Monthly Stats */}
          <div className="grid">
            <div className="stat-box success">
              <div className="stat-value">{dashboard.monthSummary.present}</div>
              <div className="stat-label">Present This Month</div>
            </div>
            <div className="stat-box danger">
              <div className="stat-value">{dashboard.monthSummary.absent}</div>
              <div className="stat-label">Absent This Month</div>
            </div>
            <div className="stat-box warning">
              <div className="stat-value">{dashboard.monthSummary.late}</div>
              <div className="stat-label">Late Arrivals</div>
            </div>
            <div className="stat-box">
              <div className="stat-value">{dashboard.monthSummary.totalHours}</div>
              <div className="stat-label">Total Hours This Month</div>
            </div>
          </div>

          {/* Recent 7 Days */}
          <div className="card">
            <div className="card-title">Recent 7 Days</div>
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Check In</th>
                    <th>Check Out</th>
                    <th>Hours</th>
                  </tr>
                </thead>
                <tbody>
                  {dashboard.recentDays && dashboard.recentDays.length > 0 ? (
                    dashboard.recentDays.map((record) => (
                      <tr key={record._id}>
                        <td>{record.date}</td>
                        <td><span className={`badge badge-${record.status.replace('-', '-')}`}>{record.status}</span></td>
                        <td>{record.checkInTime ? new Date(record.checkInTime).toLocaleTimeString() : '-'}</td>
                        <td>{record.checkOutTime ? new Date(record.checkOutTime).toLocaleTimeString() : '-'}</td>
                        <td>{record.totalHours || '-'}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" style={{ textAlign: 'center' }}>No records found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default EmployeeDashboard;
