import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axiosInstance from '../api/axios';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [dashboard, setDashboard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isCheckedIn, setIsCheckedIn] = useState(false);

  const fetchDashboard = async () => {
    try {
      const response = await axiosInstance.get('/dashboard/employee');
      const data = response.data || {};
      setDashboard({
        todayStatus: data.todayStatus || { message: 'No check-in today' },
        monthSummary: data.monthSummary || { present: 0, absent: 0, late: 0, halfDay: 0, totalHours: 0 },
        recentDays: data.recentDays || []
      });
      setError('');
      
      // Check if already checked in
      if (data.todayStatus && data.todayStatus.checkInTime && !data.todayStatus.checkOutTime) {
        setIsCheckedIn(true);
      }
    } catch (err) {
      console.error('Dashboard fetch error:', err);
      setDashboard({
        todayStatus: { message: 'No check-in today' },
        monthSummary: { present: 0, absent: 0, late: 0, halfDay: 0, totalHours: 0 },
        recentDays: []
      });
      setError('');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboard();
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const handleCheckIn = async () => {
    try {
      setError('');
      setSuccess('');
      await axiosInstance.post('/attendance/checkin');
      setSuccess('Checked in successfully!');
      setIsCheckedIn(true);
      fetchDashboard();
    } catch (err) {
      setError(err.response?.data?.error || 'Check-in failed');
    }
  };

  const handleCheckOut = async () => {
    try {
      setError('');
      setSuccess('');
      await axiosInstance.post('/attendance/checkout');
      setSuccess('Checked out successfully!');
      setIsCheckedIn(false);
      fetchDashboard();
    } catch (err) {
      setError(err.response?.data?.error || 'Check-out failed');
    }
  };

  if (loading) return <div className="emp-dashboard-container"><p>Loading...</p></div>;

  const timeStr = currentTime.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
  const userInitials = user?.name?.split(' ').map(n => n[0]).join('').toUpperCase() || 'U';

  return (
    <div className="emp-dashboard-container">
      {/* Header */}
      <div className="emp-header">
        <div className="emp-header-left">
          <h1>Employee Dashboard</h1>
          <p>Welcome back! Here's your attendance overview</p>
        </div>
        <div className="emp-header-right">
          <div className="emp-user-info">
            <h3>{user?.name || 'Employee'}</h3>
            <p>{user?.employeeId || 'N/A'} ‚Ä¢ {user?.department || 'N/A'}</p>
          </div>
          <div className="emp-user-avatar">{userInitials}</div>
        </div>
      </div>

      {error && <div className="emp-alert emp-alert-error">{error}</div>}
      {success && <div className="emp-alert emp-alert-success">{success}</div>}

      {dashboard && (
        <>
          {/* Stats Grid */}
          <div className="emp-stats-grid">
            <div className="emp-stat-card">
              <div className="emp-stat-header">
                <div>
                  <div className="emp-stat-value">{dashboard.monthSummary.present || 0}</div>
                  <div className="emp-stat-label">Present Days</div>
                </div>
                <div className="emp-stat-icon emp-stat-icon-present">‚úì</div>
              </div>
            </div>

            <div className="emp-stat-card">
              <div className="emp-stat-header">
                <div>
                  <div className="emp-stat-value">{dashboard.monthSummary.absent || 0}</div>
                  <div className="emp-stat-label">Absent Days</div>
                </div>
                <div className="emp-stat-icon emp-stat-icon-absent">‚úó</div>
              </div>
            </div>

            <div className="emp-stat-card">
              <div className="emp-stat-header">
                <div>
                  <div className="emp-stat-value">{dashboard.monthSummary.late || 0}</div>
                  <div className="emp-stat-label">Late Arrivals</div>
                </div>
                <div className="emp-stat-icon emp-stat-icon-late">‚è∞</div>
              </div>
            </div>

            <div className="emp-stat-card">
              <div className="emp-stat-header">
                <div>
                  <div className="emp-stat-value">{Math.round(dashboard.monthSummary.totalHours || 0)}</div>
                  <div className="emp-stat-label">Total Hours</div>
                </div>
                <div className="emp-stat-icon emp-stat-icon-hours">‚è±</div>
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="emp-main-content">
            {/* Recent Attendance */}
            <div className="emp-card">
              <div className="emp-card-header">
                <h2 className="emp-card-title">Recent Attendance (Last 7 Days)</h2>
                <button className="emp-btn emp-btn-primary" onClick={() => navigate('/attendance/history')}>
                  View All
                </button>
              </div>
              <table className="emp-attendance-table">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Check In</th>
                    <th>Check Out</th>
                    <th>Hours</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {dashboard.recentDays && dashboard.recentDays.length > 0 ? (
                    dashboard.recentDays.map((record) => (
                      <tr key={record._id}>
                        <td>{record.date}</td>
                        <td>{record.checkInTime ? new Date(record.checkInTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }) : '-'}</td>
                        <td>{record.checkOutTime ? new Date(record.checkOutTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }) : '-'}</td>
                        <td>{record.totalHours ? `${record.totalHours}h` : '-'}</td>
                        <td>
                          <span className={`emp-badge emp-badge-${record.status}`}>
                            {record.status.charAt(0).toUpperCase() + record.status.slice(1)}
                          </span>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" style={{ textAlign: 'center' }}>No records found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>

            {/* Today's Status & Quick Actions */}
            <div>
              {/* Status Card */}
              <div className="emp-status-card">
                <h2>Today's Status</h2>
                <div className="emp-status-time">{timeStr}</div>
                <p className="emp-status-text">
                  {isCheckedIn 
                    ? `Checked in at ${dashboard.todayStatus.checkInTime ? new Date(dashboard.todayStatus.checkInTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }) : 'N/A'}`
                    : "You haven't checked in yet"}
                </p>
                <div className="emp-status-buttons">
                  {!isCheckedIn ? (
                    <button className="emp-btn emp-btn-success" onClick={handleCheckIn}>
                      Check In
                    </button>
                  ) : (
                    <button className="emp-btn emp-btn-danger" onClick={handleCheckOut}>
                      Check Out
                    </button>
                  )}
                </div>
              </div>

              {/* Quick Actions */}
              <div className="emp-card">
                <div className="emp-card-header">
                  <h2 className="emp-card-title">Quick Actions</h2>
                </div>
                <div className="emp-quick-actions">
                  <div className="emp-action-btn" onClick={() => navigate('/attendance/history')}>
                    <div className="emp-action-icon">üìä</div>
                    <div className="emp-action-content">
                      <h4>My Attendance</h4>
                      <p>View full history</p>
                    </div>
                  </div>
                  <div className="emp-action-btn" onClick={() => navigate('/profile')}>
                    <div className="emp-action-icon">üë§</div>
                    <div className="emp-action-content">
                      <h4>My Profile</h4>
                      <p>Update information</p>
                    </div>
                  </div>
                  <div className="emp-action-btn" onClick={() => navigate('/attendance/history')}>
                    <div className="emp-action-icon">üìà</div>
                    <div className="emp-action-content">
                      <h4>Monthly Summary</h4>
                      <p>View detailed report</p>
                    </div>
                  </div>
                </div>
              </div>

              {/* Mini Calendar */}
              <div className="emp-card" style={{ marginTop: '20px' }}>
                <div className="emp-card-header">
                  <h2 className="emp-card-title">This Month</h2>
                </div>
                <div className="emp-calendar-mini">
                  {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, idx) => (
                    <div key={`header-${idx}`} className="emp-calendar-day emp-calendar-header">
                      {day}
                    </div>
                  ))}
                  {dashboard.recentDays && dashboard.recentDays.length > 0 && (
                    dashboard.recentDays.map((record, idx) => (
                      <div 
                        key={`day-${idx}`} 
                        className={`emp-calendar-day emp-calendar-${record.status}`}
                        title={`${record.date} - ${record.status}`}
                      >
                        {new Date(record.date).getDate()}
                      </div>
                    ))
                  )}
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default EmployeeDashboard;
