import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';

const AttendanceMark = () => {
  const [todayAttendance, setTodayAttendance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchTodayAttendance();
  }, []);

  const fetchTodayAttendance = async () => {
    try {
      const response = await axiosInstance.get('/attendance/today');
      setTodayAttendance(response.data);
    } catch (err) {
      if (err.response?.status === 404) {
        setTodayAttendance(null);
      } else {
        setError(err.response?.data?.error || 'Failed to fetch attendance');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCheckIn = async () => {
    try {
      setError('');
      setSuccess('');
      const response = await axiosInstance.post('/attendance/checkin');
      setTodayAttendance(response.data.attendance);
      setSuccess('Checked in successfully!');
    } catch (err) {
      setError(err.response?.data?.error || 'Check-in failed');
    }
  };

  const handleCheckOut = async () => {
    try {
      setError('');
      setSuccess('');
      const response = await axiosInstance.post('/attendance/checkout');
      setTodayAttendance(response.data.attendance);
      setSuccess('Checked out successfully!');
    } catch (err) {
      setError(err.response?.data?.error || 'Check-out failed');
    }
  };

  if (loading) return <div className="container"><p>Loading...</p></div>;

  return (
    <div className="container">
      <h1>Mark Attendance</h1>

      {error && <div className="alert alert-error">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}

      <div className="card">
        <div className="card-title">Today's Attendance</div>
        <div className="card-content">
          {!todayAttendance ? (
            <div>
              <p>No check-in yet. Click below to check in.</p>
              <button className="btn btn-success" onClick={handleCheckIn} style={{ marginTop: '1rem' }}>
                Check In
              </button>
            </div>
          ) : (
            <div>
              <p><strong>Status:</strong> {todayAttendance.status}</p>
              <p><strong>Check In:</strong> {new Date(todayAttendance.checkInTime).toLocaleTimeString()}</p>
              {todayAttendance.checkOutTime ? (
                <>
                  <p><strong>Check Out:</strong> {new Date(todayAttendance.checkOutTime).toLocaleTimeString()}</p>
                  <p><strong>Total Hours:</strong> {todayAttendance.totalHours}</p>
                </>
              ) : (
                <button className="btn btn-danger" onClick={handleCheckOut} style={{ marginTop: '1rem' }}>
                  Check Out
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AttendanceMark;
